import axios from "axios";
import { GET_ERRORS, GET_PROJECT, GET_PROJECTS } from "./types";
export const createProject = (project, history) => async (dispatch) => {
  try {
    await axios.post("http://localhost:8080/api/project/add-project", project);
    history.push("/dashboard");
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data,
    });
  }
};

export const getProjects = () => async (dispatch) => {
  const res = await axios.get("http://localhost:8080/api/project/all-project");
  dispatch({
    type: GET_PROJECTS,
    payload: res.data,
  });
};
export const getProjecdt = (id, history) => async (dispatch) => {
  const res = await axios.get(
    `http://localhost:8080/api/project/find-project/${id}`
  );
  dispatch({
    type: GET_PROJECT,
  });
};
